mapper.select:
		SELECT  NVL(COUNT(1),0)

			FROM BRST100T


            FROM BRST110T

			WHERE CRIT_YEAR = #{year}
			    AND 	DEPT_ID = #{slsDeptId}

			    AND 	EMP_ID = #{empId}

			    AND		BPLN_DST_CD = #{bplnDstCd}
			    AND		BRSL_DTL_DST_CD = #{brslDtlDstCd}

SELECT COUNT(CASE WHEN OPEN_MGMT_TGT_CD = 'B1' THEN OPEN_MGMT_TGT_CD END ) AS tgtCnt, COUNT(CASE WHEN OPEN_MGMT_TGT_CD = 'B2' THEN OPEN_MGMT_TGT_CD END ) AS dcdCnt FROM SSYS310T WHERE
OPEN_START_DATE <= TO_CHAR(SYSDATE,'YYYYMMDD') AND OPEN_END_DATE >= TO_CHAR(SYSDATE,'YYYYMMDD')
AND OPEN_MGMT_TR = #{year}

mapper.delete:
DELETE FROM BRST100T
	WHERE	CRIT_YEAR = #{critYear}
		    AND 	DEPT_ID = #{deptId}
		    AND		BPLN_DST_CD = #{bplnDstCd}
		    AND		BRSL_DTL_DST_CD = #{brslDtlDstCd}

DELETE FROM BRST110T
	WHERE	CRIT_YEAR = #{critYear}
		    AND 	DEPT_ID = #{deptId}
		    AND 	EMP_ID = #{empId}
		    AND		BPLN_DST_CD = #{bplnDstCd}
		    AND		BRSL_DTL_DST_CD = #{brslDtlDstCd}

DELETE FROM BRST100T
	WHERE	CRIT_YEAR = #{critYear}
		    AND 	DEPT_ID = #{deptId}
		    AND		BPLN_DST_CD ='P1'
		    AND		BRSL_DTL_DST_CD = #{brslDtlDstCd}

DELETE FROM BRST110T
	WHERE	CRIT_YEAR = #{critYear}
		    AND 	DEPT_ID = #{deptId}
		    AND 	EMP_ID = #{empId}
		    AND		BPLN_DST_CD ='P1'
		    AND		BRSL_DTL_DST_CD = #{brslDtlDstCd}

	DELETE FROM BRST100T
		WHERE	CRIT_YEAR = #{critYear}
			    AND 	DEPT_ID  IN (
	                            SELECT DEPT_ID FROM HMST100T
	                            WHERE ORG_CLOSE_DATE >= TO_CHAR(SYSDATE,'YYYYMMDD')
	                            START WITH
	                                DEPT_ID =  #{deptId}
	                            CONNECT BY
	                                PRIOR DEPT_ID = HGRK_DEPT_ID
                        	)
			    AND		BPLN_DST_CD ='P1'
			    AND		BRSL_DTL_DST_CD = #{brslDtlDstCd}

	DELETE FROM BRST110T
		WHERE	CRIT_YEAR = #{critYear}
			    AND 	DEPT_ID IN (
	                            SELECT DEPT_ID FROM HMST100T
	                            WHERE ORG_CLOSE_DATE >= TO_CHAR(SYSDATE,'YYYYMMDD')
	                            START WITH
	                                DEPT_ID =  #{deptId}
	                            CONNECT BY
	                                PRIOR DEPT_ID = HGRK_DEPT_ID
                        	)
			    AND		BPLN_DST_CD ='P1'
			    AND		BRSL_DTL_DST_CD = #{brslDtlDstCd}
