mapper.select:
SELECT * FROM ( SELECT COUNT(1) OVER() AS TOTAL_CNT, ROWNUM AS ROWNUMBER, T.* FROM ( SELECT LAST_CUST_ID, LAST_CUST_NM, PRJT_ID, PRJT_NM, SLS_DEPT_ID, SLS_EMP_ID, SLS_EMP_NM, CNTR_DATE, '전체' AS BSNS_CLSF_CD, '전체' AS BSNS_CLSF_CD_NM, -- 사업분류코드 WCT_PRBB_CD, WCT_PRBB_CD_NM, -- 수주확률코드 BOPT_STAT_CD, BOPT_STAT_CD_NM, -- 사업기회상태코드 SELL_OPPTY_RSL_AMT, -- 매출금액_기회 BUY_OPPTY_RSL_AMT, -- 매입금액_기회 SELL_FCST_RSL_AMT, -- 매출금액_확률적용 BUY_FCST_RSL_AMT, -- 매입금액_확률적용 SELL_RSL_AMT, -- 매출금액_실적 REAL_SELL_RSL_AMT, -- 매출금액_실제실적 BUY_RSL_AMT, -- 매입금액_실적 REAL_BUY_RSL_AMT, -- 매입금액_실제실적 BOPT_ID, BOPT_NM, BOPT_END_TYPE_CD, BOPT_END_TYPE_CD_NM, COM_CD_NUM_VAL, SUM(SELL_OPPTY_RSL_AMT) OVER() AS TOTAL_SELL_OPPTY_RSL_AMT, -- 매출금액_기회 총액 SUM(BUY_OPPTY_RSL_AMT) OVER() AS TOTAL_BUY_OPPTY_RSL_AMT, -- 매입금액_기회 총액 SUM(SELL_FCST_RSL_AMT) OVER() AS TOTAL_SELL_FCST_RSL_AMT, -- 매출금액_확률적용 총액 SUM(BUY_FCST_RSL_AMT) OVER() AS TOTAL_BUY_FCST_RSL_AMT, -- 매입금액_확률적용 총액 SUM(SELL_RSL_AMT) OVER() AS TOTAL_SELL_RSL_AMT, -- 매출금액_실적 총액 SUM(REAL_SELL_RSL_AMT) OVER() AS TOTAL_REAL_SELL_RSL_AMT, -- 매출금액_실제실적 총액 SUM(BUY_RSL_AMT) OVER() AS TOTAL_BUY_RSL_AMT, -- 매입금액_실적 총액 SUM(REAL_BUY_RSL_AMT) OVER() AS TOTAL_REAL_BUY_RSL_AMT -- 매입금액_실제실적 총액 FROM ( SELECT P.LAST_CUST_ID, M.CUST_NM LAST_CUST_NM, P.PRJT_ID, P.PRJT_NM, P.SLS_DEPT_ID, P.SLS_EMP_ID, H.EMP_NM SLS_EMP_NM, P.CNTR_DATE, WCT_PRBB_CD, IBIZ_COM_CD_NM('WCT_PRBB_CD',P.WCT_PRBB_CD) WCT_PRBB_CD_NM, -- 수주확률코드 BOPT_STAT_CD, IBIZ_COM_CD_NM('BOPT_STAT_CD',P.BOPT_STAT_CD) BOPT_STAT_CD_NM, -- 사업기회상태코드 P.SELL_AMT AS SELL_OPPTY_RSL_AMT, -- 매출금액_기회 P.BUY_AMT AS BUY_OPPTY_RSL_AMT, -- 매입금액_기회 P.SELL_AMT*P.COM_CD_NUM_VAL AS SELL_FCST_RSL_AMT, -- 매출금액_확률적용 P.BUY_AMT*P.COM_CD_NUM_VAL AS BUY_FCST_RSL_AMT, -- 매입금액_확률적용 DECODE(BOPT_STAT_CD,'E',P.SELL_AMT,0) AS SELL_RSL_AMT, -- 매출금액_실적 NVL(RSL.SUM_SELL_AMT,0) AS REAL_SELL_RSL_AMT, -- 매출금액_실제실적 DECODE(BOPT_STAT_CD,'E',P.BUY_AMT,0) AS BUY_RSL_AMT, -- 매입금액_실적 NVL(RSL.ACML_BUY_RSL_AMT,0) AS REAL_BUY_RSL_AMT, -- 매입금액_실제실적 P.BOPT_ID, P.BOPT_NM, P.BOPT_END_TYPE_CD, IBIZ_COM_CD_NM('BOPT_END_TYPE_CD', P.BOPT_END_TYPE_CD) BOPT_END_TYPE_CD_NM, P.COM_CD_NUM_VAL FROM CMST000T M JOIN ( SELECT C.LAST_CUST_ID, C.PRJT_ID, C.PRJT_NM, A.BOPT_ID, A.BOPT_NM, -- A.BOPT_NM, A.SLS_DEPT_ID, A.SLS_EMP_ID, A.CNTR_DATE, A.WCT_DCD_DATE, -- 계약일자, 수주확정일자 A.BOPT_END_TYPE_CD, A.WCT_PRBB_CD, A.BOPT_STAT_CD, S.COM_CD_NUM_VAL, NVL(SUM(B.SELL_AMT),0) SELL_AMT, -- 매출금액 NVL(SUM(B.BUY_AMT),0) BUY_AMT -- 매입금액 FROM BPIP000T A, BPIP020T B, APRJ000T C, ACOM010T S WHERE A.BOPT_ID = B.BOPT_ID(+) AND A.BOPT_STAT_CD != 'D1' AND (A.BOPT_STAT_CD = 'E' -- 계약완료 OR (A.FCST_RFLC_YN = 'Y' -- 사업예상반영여부 AND A.BOPT_STAT_CD != 'F'))-- 사업기회종료 제외 AND A.PRJT_ID = C.PRJT_ID AND C.PRJT_STAT_CD NOT LIKE 'F%' --F1.사업이관, F2.사업폐기 AND C.PRJT_STAT_CD NOT LIKE 'D%' --D1.일반삭제, D2.중복삭제 AND S.COM_GRP_CD = (SELECT COM_GRP_CD FROM ACOM020T WHERE CLMN_NM = 'WCT_PRBB_CD') -- 수주확률코드 AND A.WCT_PRBB_CD = S.COM_CD
AND C.PRJT_TYPE_CD = #{prjtTypeCd}
AND SUBSTR(A.CNTR_DATE,1,6) BETWEEN #{fromDate} AND #{toDate} -- 조회조건_기간
AND A.SLS_DEPT_ID IN ( SELECT DEPT_ID FROM HMST110T WHERE SELL_OCR_YN = 'Y' -- 매출발생부서
AND SUBSTR(ORG_CLOSE_DATE,1,4) >= #{critYear}
AND CRIT_YEAR = #{critYear} START WITH DEPT_ID = #{slsDeptId} CONNECT BY PRIOR DEPT_ID = HGRK_DEPT_ID )
AND A.SLS_EMP_ID = #{slsEmpId} -- 조회조건_영업대표
GROUP BY C.LAST_CUST_ID, C.PRJT_ID, C.PRJT_NM, A.BOPT_ID, A.BOPT_NM, A.SLS_DEPT_ID, A.SLS_EMP_ID, A.CNTR_DATE, A.WCT_DCD_DATE, -- 계약일자, 수주확정일자 A.BOPT_END_TYPE_CD, A.WCT_PRBB_CD, A.BOPT_STAT_CD, S.COM_CD_NUM_VAL ) P ON P.LAST_CUST_ID = M.CUST_ID JOIN HMST300T H ON P.SLS_EMP_ID = H.EMP_ID LEFT JOIN ( SELECT WCT_RSL.*, BUY_RSL.ACML_BUY_RSL_AMT FROM ( SELECT BOPT_ID, CNTR_YAM, PRJT_ID, SUM(ROUND(NVL(RSL.RATE*CNTR.CNTR_AMT,0),0)) AS SUM_SELL_AMT FROM ( SELECT BEST.BOPT_ID, BEST.FCST_PAL_ID, (SELECT HGRK_COM_CD FROM ACOM010T WHERE COM_GRP_CD ='PM04' AND COM_CD = BEST.PROD_TYPE_CD) AS PROD_DST_CD, BEST.PROD_TYPE_CD, BEST.GODS_CLSF_CD, BEST.SELL_AMT , SUM_BEST.SUM_SELL_AMT, CASE WHEN NVL(SUM_BEST.SUM_SELL_AMT,0) = 0 THEN 0 ELSE NVL(BEST.SELL_AMT/SUM_BEST.SUM_SELL_AMT,0) END AS RATE FROM ( SELECT BE02.FCST_PAL_ID,SUM(BE02.SELL_AMT) AS SUM_SELL_AMT FROM BEST020T BE02 WHERE 1=1 AND GODS_CLSF_CD != 'AL' GROUP BY BE02.FCST_PAL_ID ) SUM_BEST JOIN ( SELECT BE00.BOPT_ID, BE02.FCST_PAL_ID, BE02.PROD_TYPE_CD, BE02.GODS_CLSF_CD, BE02.SELL_AMT FROM BEST020T BE02 JOIN BEST000T BE00 ON BE00.FCST_PAL_ID = BE02.FCST_PAL_ID WHERE 1=1 AND GODS_CLSF_CD != 'AL' ) BEST ON SUM_BEST.FCST_PAL_ID = BEST.FCST_PAL_ID ) RSL -- BEST020T 기반 비율 LEFT JOIN ( SELECT PRJT_ID, CNTR_ID, FCST_PAL_ID, SUM(CNTR_AMT) AS CNTR_AMT, SUBSTR(CNTR_DATE,1,6) AS CNTR_YAM, SLS_DEPT_ID, SLS_EMP_ID FROM WMST000T CNTR GROUP BY PRJT_ID, CNTR_ID, FCST_PAL_ID, SUBSTR(CNTR_DATE,1,6), SLS_DEPT_ID, SLS_EMP_ID ) CNTR -- 계약금액, 계약정보 ON RSL.FCST_PAL_ID = CNTR.FCST_PAL_ID JOIN (SELECT HGRK_COM_CD, COM_CD FROM ACOM010T WHERE COM_GRP_CD = 'PM08') GC ON GC.COM_CD = RSL.GODS_CLSF_CD WHERE ROUND(NVL(RSL.RATE*CNTR.CNTR_AMT,0),0) != 0
AND CNTR_YAM <= #{toDate}
GROUP BY BOPT_ID, CNTR_YAM, PRJT_ID ) WCT_RSL LEFT JOIN ( SELECT WCT.BOPT_ID, SUM(NVL(RSL_BUY_AMT,0)) AS RSL_BUY_AMT, NVL(ACML_BUY_RSL_AMT,0) AS ACML_BUY_RSL_AMT FROM RSLT000V RSL LEFT JOIN ( SELECT WCT.WCT_ID, CNTR.CNTR_ID, BEST.BOPT_ID, CNTR.SLS_DEPT_ID FROM WMST100T WCT RIGHT JOIN WMST000T CNTR ON WCT.CNTR_ID = CNTR.CNTR_ID JOIN BEST000T BEST ON BEST.FCST_PAL_ID = CNTR.FCST_PAL_ID ) WCT ON WCT.WCT_ID = RSL.WCT_ID JOIN APRJ000T APRJ ON RSL.PRJT_ID = APRJ.PRJT_ID JOIN ( SELECT PRJT_ID, SUM(RSL_BUY_AMT) AS ACML_BUY_RSL_AMT FROM RSLT000V SRSL
WHERE CRIT_YAM <= #{toDate}
AND SUBSTR(CRIT_YAM,1,4) = #{critYear} GROUP BY PRJT_ID ) SUM_RSL ON SUM_RSL.PRJT_ID = RSL.PRJT_ID WHERE RSL_BUY_AMT != 0 -- 매입 GROUP BY WCT.BOPT_ID,ACML_BUY_RSL_AMT ) BUY_RSL ON BUY_RSL.BOPT_ID = WCT_RSL.BOPT_ID
) RSL -- 실제 실적 ON (RSL.BOPT_ID = P.BOPT_ID AND SUBSTR(P.CNTR_DATE,1,6) = RSL.CNTR_YAM)
) ORDER BY CNTR_DATE, PRJT_ID -- 프로젝트ID ) T ) S WHERE ROWNUMBER BETWEEN #{pageSize} * (#{pageNumber} - 1) + 1 AND #{pageSize} * (#{pageNumber} - 1) + #{pageSize}

SELECT ROWNUMBER, LAST_CUST_ID ,LAST_CUST_NM, PRJT_ID,BOPT_ID,BOPT_NM, ORDE_CUST_ID, ORDE_CUST_NM,CNTR_DATE, CNTR_AMT,CNTR_TRSF_START_YAM,CNTR_TRSF_END_YAM,SUM_SELL_AMT, WCT_PRBB_CD ,WCT_PRBB_CD_NM, SLS_DEPT_ID, SLS_DEPT_NM, SLS_EMP_ID, SLS_EMP_NM, BOPT_STAT_CD, BOPT_STAT_CD_NM, TOTAL_CNT FROM ( SELECT ROWNUM AS ROWNUMBER, LAST_CUST_ID ,LAST_CUST_NM, PRJT_ID,BOPT_ID,BOPT_NM, ORDE_CUST_ID, ORDE_CUST_NM,CNTR_DATE, CNTR_AMT, CNTR_TRSF_START_YAM,CNTR_TRSF_END_YAM,SUM_SELL_AMT, WCT_PRBB_CD ,WCT_PRBB_CD_NM, SLS_DEPT_ID, SLS_DEPT_NM, SLS_EMP_ID, SLS_EMP_NM, BOPT_STAT_CD, BOPT_STAT_CD_NM, TOTAL_CNT FROM ( SELECT T2.LAST_CUST_ID ,T2.LAST_CUST_NM,T1.PRJT_ID,T1.BOPT_ID,T1.BOPT_NM, T1.ORDE_CUST_ID,IBIZ_CUST_NM(T1.ORDE_CUST_ID) AS ORDE_CUST_NM, T1.CNTR_DATE, T1.CNTR_AMT,T1.CNTR_TRSF_START_YAM,T1.CNTR_TRSF_END_YAM, ( SELECT SUM(SELL_AMT) FROM BPIP020T WHERE BOPT_ID = T1.BOPT_ID ) SUM_SELL_AMT, T1.WCT_PRBB_CD ,IBIZ_COM_CD_NM('WCT_PRBB_CD',T1.WCT_PRBB_CD) WCT_PRBB_CD_NM, T1.SLS_DEPT_ID,IBIZ_DEPT_NM(T1.SLS_DEPT_ID) AS SLS_DEPT_NM, T1.SLS_EMP_ID,IBIZ_EMP_NM(T1.SLS_EMP_ID) AS SLS_EMP_NM, T1.BOPT_STAT_CD,IBIZ_COM_CD_NM('BOPT_STAT_CD',T1.BOPT_STAT_CD) BOPT_STAT_CD_NM, COUNT(1) OVER() AS TOTAL_CNT FROM BPIP000T T1 JOIN ( SELECT PRJT_ID,PRJT_NM,LAST_CUST_ID,PRJT_STAT_CD,IBIZ_CUST_NM(LAST_CUST_ID) AS LAST_CUST_NM FROM APRJ000T WHERE PRJT_STAT_CD NOT LIKE 'F%' AND PRJT_STAT_CD NOT LIKE 'D%' ) T2 ON T1.PRJT_ID = T2.PRJT_ID WHERE 1=1 AND T1.BOPT_STAT_CD != 'D1' AND T1.DTIM_RPRT_DISP_YN = 'Y' AND T1.SLS_DEPT_ID IN (SELECT DEPT_ID FROM HMST100T START WITH
DEPT_ID = #{slsDeptId}
HGRK_DEPT_ID IS NULL
CONNECT BY PRIOR DEPT_ID=HGRK_DEPT_ID )
AND UPPER(T2.LAST_CUST_NM) LIKE '%'||UPPER(#{lastCustNm})||'%'
AND UPPER(T2.LAST_CUST_ID) = UPPER(#{lastCustId})
AND UPPER(T2.PRJT_NM) LIKE '%'||UPPER(#{prjtNm})||'%'
AND UPPER(T2.PRJT_ID) = UPPER(#{prjtId})
AND UPPER(T1.SLS_EMP_ID) LIKE '%'||UPPER(#{slsEmpId})||'%'
AND EXISTS (SELECT 1 FROM BPIP250T WHERE BOPT_ID = T1.BOPT_ID
AND #{regActStartDate} <= TO_CHAR(REG_DT, 'YYYYMMDD')
AND #{regActEndDate} >= TO_CHAR(REG_DT, 'YYYYMMDD')
)
AND T1.BOPT_STAT_CD = #{boptStatCd}
AND #{minWctPrbbCd} <= TO_NUMBER(T1.WCT_PRBB_CD)
AND #{maxWctPrbbCd} >= TO_NUMBER(T1.WCT_PRBB_CD)
AND NVL(( SELECT SUM(SELL_AMT) FROM BPIP020T WHERE BOPT_ID = T1.BOPT_ID
),0) >= #{cntrAmt} * 100000000
),0) <= #{cntrAmt} * 100000000
),0) > #{cntrAmt} * 100000000
),0) < #{cntrAmt} * 100000000
ORDER BY T1.BOPT_NM, SUM_SELL_AMT DESC, T1.CNTR_DATE )TF ) WT WHERE ROWNUMBER BETWEEN #{pageSize} * (#{pageNumber} - 1) + 1 AND #{pageSize} * (#{pageNumber} - 1) + #{pageSize}

SELECT T250.BOPT_ID,  T000.SPCL_CONT, T250.PRMRY_ACT_CONT,  T250.ACT_PLAN_CONT
FROM BPIP250T T250
LEFT OUTER JOIN BPIP000T T000
ON T000.BOPT_ID = T250.BOPT_ID
WHERE 1 = 1
AND T250.BOPT_ID = #{boptId}



        AND TO_CHAR(T250.REG_DT, 'YYYYMMDD') >= #{regActStartDate}




        AND TO_CHAR(T250.REG_DT, 'YYYYMMDD') <= #{regActEndDate}


ORDER BY T250.REG_DT DESC, T250.CHG_DT DESC

SELECT CRIT_YAM,SLS_DEPT_ID, SLS_DEPT_NM, REG_EMP_ID, REG_EMP_NM,REG_DT, CHG_EMP_ID, CHG_EMP_NM, CHG_DT, DEPT_SORT_ROWNUM,TOTAL_CNT, ROWNUMBER FROM ( SELECT CRIT_YAM,SLS_DEPT_ID, SLS_DEPT_NM, REG_EMP_ID, REG_EMP_NM,REG_DT, CHG_EMP_ID, CHG_EMP_NM, CHG_DT, DEPT_SORT_ROWNUM,TOTAL_CNT, ROWNUM AS ROWNUMBER FROM ( SELECT BPIP.CRIT_YAM, BPIP.SLS_DEPT_ID,HMST.DEPT_NM AS SLS_DEPT_NM, BPIP.REG_EMP_ID, IBIZ_EMP_NM(BPIP.REG_EMP_ID) AS REG_EMP_NM, BPIP.REG_DT, BPIP.CHG_EMP_ID,IBIZ_EMP_NM(BPIP.CHG_EMP_ID) AS CHG_EMP_NM, BPIP.CHG_DT, HMST.DEPT_SORT_ROWNUM, COUNT(1) OVER() AS TOTAL_CNT FROM BPIP260T BPIP JOIN ( SELECT DEPT_ID, DEPT_NM, HGRK_DEPT_ID, ROWNUM AS DEPT_SORT_ROWNUM FROM HMST100T START WITH HGRK_DEPT_ID IS NULL CONNECT BY PRIOR DEPT_ID = HGRK_DEPT_ID ORDER SIBLINGS BY DEPT_SORT_SEQC ASC ) HMST ON BPIP.SLS_DEPT_ID = HMST.DEPT_ID WHERE 1 = 1
AND ( BPIP.SLS_DEPT_ID IN ( SELECT DEPT_ID FROM HMST100T START WITH HGRK_DEPT_ID = #{slsDeptId} CONNECT BY PRIOR DEPT_ID = HGRK_DEPT_ID ) OR BPIP.SLS_DEPT_ID = #{slsDeptId} )
AND SUBSTR(BPIP.CRIT_YAM,0,4) = #{critYear}
ORDER BY CRIT_YAM DESC, DEPT_SORT_ROWNUM ASC ) TF ) WHERE ROWNUMBER BETWEEN #{pageSize} * (#{pageNumber} - 1) + 1 AND #{pageSize} * (#{pageNumber} - 1) + #{pageSize}

SELECT NVL(COUNT(1),0)
FROM
BPIP260T
WHERE
CRIT_YAM = #{critYam}
AND   SLS_DEPT_ID = #{slsDeptId}

mapper.update:
UPDATE BPRJ010T
SET RPRT_YN = #{rprtYn},

    RPRT_CONT = #{rprtCont},


    BSNS_PRGS_STAT_CD = #{bsnsPrgsStatCd},

    CHG_EMP_ID = #{chgEmpId},
    CHG_DT = SYSDATE
WHERE
    PRJT_ID = #{prjtId}
    AND SEQ = #{seq}
    AND BSNS_RPRT_START_DATE = #{bsnsRprtStartDate}

mapper.delete:
DELETE FROM
BPIP260T
WHERE
SLS_DEPT_ID = #{slsDeptId}
AND   CRIT_YAM = #{critYam}

DELETE FROM BPRJ000T
WHERE PRJT_ID = #{prjtId}
AND SEQ = #{seq}

DELETE FROM BPRJ010T
WHERE PRJT_ID = #{prjtId}
    AND SEQ = #{seq}

    AND BSNS_RPRT_START_DATE = #{bsnsRprtStartDate}
