mapper.select:
SELECT ROWNUMBER,BSNS_CLSF_CD,BSNS_CLSF_CD_NM, LAST_CUST_ID ,LAST_CUST_NM, PRJT_ID,BOPT_ID,BOPT_NM, ORDE_CUST_ID, ORDE_CUST_NM,CNTR_DATE, CNTR_AMT,CNTR_TRSF_START_YAM,CNTR_TRSF_END_YAM,SUM_SELL_AMT, WCT_PRBB_CD ,WCT_PRBB_CD_NM, SLS_DEPT_ID, SLS_DEPT_NM, SLS_EMP_ID, SLS_EMP_NM, BOPT_STAT_CD, BOPT_STAT_CD_NM, TOTAL_CNT FROM ( SELECT ROWNUM AS ROWNUMBER,BSNS_CLSF_CD,BSNS_CLSF_CD_NM, LAST_CUST_ID ,LAST_CUST_NM, PRJT_ID,BOPT_ID,BOPT_NM, ORDE_CUST_ID, ORDE_CUST_NM,CNTR_DATE, CNTR_AMT, CNTR_TRSF_START_YAM,CNTR_TRSF_END_YAM,SUM_SELL_AMT, WCT_PRBB_CD ,WCT_PRBB_CD_NM, SLS_DEPT_ID, SLS_DEPT_NM, SLS_EMP_ID, SLS_EMP_NM, BOPT_STAT_CD, BOPT_STAT_CD_NM, TOTAL_CNT FROM ( SELECT T1.BSNS_CLSF_CD ,IBIZ_COM_CD_NM('BSNS_CLSF_CD',T1.BSNS_CLSF_CD) BSNS_CLSF_CD_NM, T2.LAST_CUST_ID ,T2.LAST_CUST_NM,T1.PRJT_ID,T1.BOPT_ID,T1.BOPT_NM, T1.ORDE_CUST_ID,IBIZ_CUST_NM(T1.ORDE_CUST_ID) AS ORDE_CUST_NM, T1.CNTR_DATE, T1.CNTR_AMT,T1.CNTR_TRSF_START_YAM,T1.CNTR_TRSF_END_YAM, ( SELECT SUM(SELL_AMT) FROM BPIP020T WHERE BOPT_ID = T1.BOPT_ID ) SUM_SELL_AMT, T1.WCT_PRBB_CD ,IBIZ_COM_CD_NM('WCT_PRBB_CD',T1.WCT_PRBB_CD) WCT_PRBB_CD_NM, T1.SLS_DEPT_ID,IBIZ_DEPT_NM(T1.SLS_DEPT_ID) AS SLS_DEPT_NM, T1.SLS_EMP_ID,IBIZ_EMP_NM(T1.SLS_EMP_ID) AS SLS_EMP_NM, T1.BOPT_STAT_CD,IBIZ_COM_CD_NM('BOPT_STAT_CD',T1.BOPT_STAT_CD) BOPT_STAT_CD_NM, COUNT(1) OVER() AS TOTAL_CNT FROM BPIP000T T1 JOIN ( SELECT PRJT_ID,PRJT_NM,LAST_CUST_ID,PRJT_STAT_CD,IBIZ_CUST_NM(LAST_CUST_ID) AS LAST_CUST_NM FROM APRJ000T WHERE PRJT_STAT_CD NOT LIKE 'F%' AND PRJT_STAT_CD NOT LIKE 'D%' ) T2 ON T1.PRJT_ID = T2.PRJT_ID WHERE 1=1 AND T1.BOPT_STAT_CD != 'D1' AND T1.DTIM_RPRT_DISP_YN = 'Y' AND T1.SLS_DEPT_ID IN (SELECT DEPT_ID FROM HMST100T START WITH
DEPT_ID = #{slsDeptId}
HGRK_DEPT_ID IS NULL
CONNECT BY PRIOR DEPT_ID=HGRK_DEPT_ID )
AND UPPER(T2.LAST_CUST_NM) LIKE '%'||UPPER(#{lastCustNm})||'%'
AND UPPER(T2.LAST_CUST_ID) = UPPER(#{lastCustId})
AND UPPER(T2.PRJT_NM) LIKE '%'||UPPER(#{prjtNm})||'%'
AND UPPER(T2.PRJT_ID) = UPPER(#{prjtId})
AND UPPER(T1.SLS_EMP_ID) LIKE '%'||UPPER(#{slsEmpId})||'%'
AND EXISTS (SELECT 1 FROM BPIP250T WHERE BOPT_ID = T1.BOPT_ID
AND #{regActStartDate} <= TO_CHAR(REG_DT, 'YYYYMMDD')
AND #{regActEndDate} >= TO_CHAR(REG_DT, 'YYYYMMDD')
)
AND T1.BOPT_STAT_CD = #{boptStatCd}
AND #{minWctPrbbCd} <= TO_NUMBER(T1.WCT_PRBB_CD)
AND #{maxWctPrbbCd} >= TO_NUMBER(T1.WCT_PRBB_CD)
AND NVL(( SELECT SUM(SELL_AMT) FROM BPIP020T WHERE BOPT_ID = T1.BOPT_ID
),0) >= #{cntrAmt} * 100000000
),0) <= #{cntrAmt} * 100000000
),0) > #{cntrAmt} * 100000000
),0) < #{cntrAmt} * 100000000
ORDER BY T1.BSNS_CLSF_CD, SUM_SELL_AMT DESC, T1.CNTR_DATE )TF ) WT WHERE ROWNUMBER BETWEEN #{pageSize} * (#{pageNumber} - 1) + 1 AND #{pageSize} * (#{pageNumber} - 1) + #{pageSize}

SELECT T250.BOPT_ID,  T000.SPCL_CONT, T250.PRMRY_ACT_CONT,  T250.ACT_PLAN_CONT
FROM BPIP250T T250
LEFT OUTER JOIN BPIP000T T000
ON T000.BOPT_ID = T250.BOPT_ID
WHERE 1 = 1
AND T250.BOPT_ID = #{boptId}



        AND TO_CHAR(T250.REG_DT, 'YYYYMMDD') >= #{regActStartDate}




        AND TO_CHAR(T250.REG_DT, 'YYYYMMDD') <= #{regActEndDate}


ORDER BY T250.REG_DT DESC, T250.CHG_DT DESC

SELECT CRIT_YAM,SLS_DEPT_ID, SLS_DEPT_NM, REG_EMP_ID, REG_EMP_NM,REG_DT, CHG_EMP_ID, CHG_EMP_NM, CHG_DT, DEPT_SORT_ROWNUM,TOTAL_CNT, ROWNUMBER FROM ( SELECT CRIT_YAM,SLS_DEPT_ID, SLS_DEPT_NM, REG_EMP_ID, REG_EMP_NM,REG_DT, CHG_EMP_ID, CHG_EMP_NM, CHG_DT, DEPT_SORT_ROWNUM,TOTAL_CNT, ROWNUM AS ROWNUMBER FROM ( SELECT BPIP.CRIT_YAM, BPIP.SLS_DEPT_ID,HMST.DEPT_NM AS SLS_DEPT_NM, BPIP.REG_EMP_ID, IBIZ_EMP_NM(BPIP.REG_EMP_ID) AS REG_EMP_NM, BPIP.REG_DT, BPIP.CHG_EMP_ID,IBIZ_EMP_NM(BPIP.CHG_EMP_ID) AS CHG_EMP_NM, BPIP.CHG_DT, HMST.DEPT_SORT_ROWNUM, COUNT(1) OVER() AS TOTAL_CNT FROM BPIP260T BPIP JOIN ( SELECT DEPT_ID, DEPT_NM, HGRK_DEPT_ID, ROWNUM AS DEPT_SORT_ROWNUM FROM HMST100T START WITH HGRK_DEPT_ID IS NULL CONNECT BY PRIOR DEPT_ID = HGRK_DEPT_ID ORDER SIBLINGS BY DEPT_SORT_SEQC ASC ) HMST ON BPIP.SLS_DEPT_ID = HMST.DEPT_ID WHERE 1 = 1
AND ( BPIP.SLS_DEPT_ID IN ( SELECT DEPT_ID FROM HMST100T START WITH HGRK_DEPT_ID = #{slsDeptId} CONNECT BY PRIOR DEPT_ID = HGRK_DEPT_ID ) OR BPIP.SLS_DEPT_ID = #{slsDeptId} )
AND SUBSTR(BPIP.CRIT_YAM,0,4) = #{critYear}
ORDER BY CRIT_YAM DESC, DEPT_SORT_ROWNUM ASC ) TF ) WHERE ROWNUMBER BETWEEN #{pageSize} * (#{pageNumber} - 1) + 1 AND #{pageSize} * (#{pageNumber} - 1) + #{pageSize}

SELECT NVL(COUNT(1),0)
FROM
BPIP260T
WHERE
CRIT_YAM = #{critYam}
AND   SLS_DEPT_ID = #{slsDeptId}

SELECT * FROM ( SELECT COUNT(1) OVER() AS TOTAL_CNT, ROWNUM AS ROWNUMBER, T.* FROM ( SELECT * FROM ( SELECT SUBSTR(CNTR_DATE ,1,6) AS SELL_CRIT_YAM, C.BLTO_DEPT_ID AS SLS_DEPT_ID,DEPT.DEPT_NM,PRJT.SLS_EMP_ID,EMP.EMP_NM ,CNTR_ID ,A.PRJT_ID ,PRJT.PRJT_NM ,PRJT.PRJT_TYPE_CD ,A.FCST_PAL_ID ,'R1' BPLN_DST_CD -- R1.사업실적 ,'AR' BRSL_DTL_DST_CD -- AR.수주실적 ,BSNS_CLSF_CD ,F.HGRK_COM_CD AS PROD_DST_CD ,IBIZ_COM_CD_NM('PROD_DST_CD',F.HGRK_COM_CD) PROD_DST_CD_NM ,SUBSTR(E.P_TYPE,1,2) AS PROD_TYPE_CD ,SUBSTR(E.P_TYPE,2,2) AS GODS_CLSF_CD ,PRJT.LAST_CUST_ID,CUST.CUST_NM AS LAST_CUST_NM ,ROUND(DECODE(E.RATE,NULL,A.CNTR_AMT, A.CNTR_AMT*E.RATE),0) AS CNTR_AMT FROM WMST000T A LEFT JOIN APRJ000T B ON A.PRJT_ID = B.PRJT_ID LEFT JOIN HMST300T C ON B.SLS_EMP_ID = C.EMP_ID LEFT JOIN APRJ000T PRJT ON A.PRJT_ID = PRJT.PRJT_ID LEFT JOIN HMST300T EMP ON A.SLS_EMP_ID = EMP.EMP_ID LEFT JOIN HMST100T DEPT ON EMP.BLTO_DEPT_ID = DEPT.DEPT_ID LEFT JOIN (SELECT B.FCST_PAL_ID, B.P_TYPE, SELL_AMT/SUM_AMT AS RATE, NVL(DECODE(SUM_BUY_AMT,NVL(NULL,0),0,BUY_AMT/SUM_BUY_AMT),0) AS BUY_RATE FROM ( SELECT FCST_PAL_ID,SUM(SELL_AMT) AS SUM_AMT,SUM(BUY_AMT) AS SUM_BUY_AMT FROM TT000V GROUP BY FCST_PAL_ID )A LEFT JOIN TT000V B ON A.FCST_PAL_ID = B.FCST_PAL_ID WHERE SELL_AMT/SUM_AMT IS NOT NULL )E ON A.FCST_PAL_ID = E.FCST_PAL_ID LEFT JOIN (SELECT * FROM ACOM010T WHERE COM_GRP_CD = 'PM04')F ON SUBSTR(E.P_TYPE,1,2) = F.COM_CD LEFT JOIN CMST000T CUST ON PRJT.LAST_CUST_ID = CUST.CUST_ID )A WHERE 1=1 AND CNTR_AMT != 0 AND SELL_CRIT_YAM BETWEEN #{fromDate} AND #{toDate} -- 조회조건_기간
AND PROD_DST_CD = #{prodDstCd} -- 조회조건_상품구분
AND BSNS_CLSF_CD = #{bsnsClsfCd} -- 조회조건_사업분류
AND EMP_ID = #{slsEmpId} -- 조회조건_영업대표
AND A.SLS_DEPT_ID in ( SELECT DEPT_ID FROM HMST110T WHERE SELL_OCR_YN = 'Y' -- 매출발생부서
AND SUBSTR(ORG_CLOSE_DATE,1,4) >= #{critYear}
AND CRIT_YEAR = #{critYear} START WITH DEPT_ID = #{slsDeptId} CONNECT BY PRIOR DEPT_ID = HGRK_DEPT_ID ) -- 조회조건_영업부서
AND A.PRJT_TYPE_CD = #{prjtTypeCd}
ORDER BY LAST_CUST_NM, PRJT_NM -- 정렬기준: 고객명+프로젝트명
ORDER BY SELL_CRIT_YAM, LAST_CUST_NM, PRJT_NM -- 정렬기준: 발생시기
ORDER BY CNTR_AMT DESC, LAST_CUST_NM, PRJT_NM -- 정렬기준: 실적금액
) T ) S WHERE ROWNUMBER BETWEEN #{pageSize} * (#{pageNumber} - 1) + 1 AND #{pageSize} * (#{pageNumber} - 1) + #{pageSize}

mapper.update:
UPDATE BPRJ010T
SET RPRT_YN = #{rprtYn},

    RPRT_CONT = #{rprtCont},


    BSNS_PRGS_STAT_CD = #{bsnsPrgsStatCd},

    CHG_EMP_ID = #{chgEmpId},
    CHG_DT = SYSDATE
WHERE
    PRJT_ID = #{prjtId}
    AND SEQ = #{seq}
    AND BSNS_RPRT_START_DATE = #{bsnsRprtStartDate}

mapper.delete:
DELETE FROM
BPIP260T
WHERE
SLS_DEPT_ID = #{slsDeptId}
AND   CRIT_YAM = #{critYam}

DELETE FROM BPRJ000T
WHERE PRJT_ID = #{prjtId}
AND SEQ = #{seq}

DELETE FROM BPRJ010T
WHERE PRJT_ID = #{prjtId}
    AND SEQ = #{seq}

    AND BSNS_RPRT_START_DATE = #{bsnsRprtStartDate}
